add_action( 'wp_enqueue_scripts', 'quadlayers_enqueue_css' );

function quadlayers_enqueue_css(){    
	if( is_shop() ) :
    wp_enqueue_style(  'checkout_style',
        get_stylesheet_directory_uri() . '/shop_style.css'
    );
	endif;
}




